const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    const endpoint = 'http://localhost:8100/';
    fetchMovies(endpoint);
  }, []);

  const fetchMovies = async (endpoint) => {
    try {
      const response = await fetch(endpoint);
      const data = await response.json();
      console.log('Fetched Movies:', data); // Log fetched movies
      setMovies(data);
      setFilteredMovies(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movies:', error);
      setLoading(false);
    }
  };

  const handleFilterChange = async ({ filterBy, value }) => {
    try {
      const endpoint = `http://localhost:8100/filter?${encodeURIComponent(filterBy)}=${encodeURIComponent(value)}`;
      console.log('Filtering with endpoint:', endpoint); // Log filter endpoint
      const response = await fetch(endpoint);
      const filteredData = await response.json();
      console.log('Filtered Movies:', filteredData); // Log filtered movies
      setFilteredMovies(filteredData);
    } catch (error) {
      console.error('Error fetching filtered movies:', error);
    }
  };

  const headingStyle = {
    fontFamily: 'Georgia, serif',
    fontSize: '2rem',
    fontWeight: 'bold',
    color: 'Maroon',
  };

  const footerStyle = {
    marginTop: '20px', 
    textAlign: 'center',
  };

  return (
    <Router>
      <div className="app-container">
        <AppNavbar />
        <Routes>
          <Route path="/add-movie" element={<MovieForm />} />
          <Route path='/update-movie' element={<UpdateMovieForm />} />
          <Route
            path="/"
            element={(
              <>
                <Search />
                <Filter onFilterChange={handleFilterChange} movies={movies} setFilteredMovies={setFilteredMovies} />
                {loading ? (
                  <p className="text-center">Loading...</p>
                ) : (
                  filteredMovies.length > 0 ? (
                    <MovieList movies={filteredMovies} />
                  ) : (
                    <p className="text-center">No movies found.</p> // Handle empty filtered movies
                  )
                )}
              </>
            )}
          />
        </Routes>
        <div className="custom-dark-olive" style={footerStyle}>
          <p className='title'>Contact Details:</p>
          <p className='label_color'>Email: ahamedhuzair13@gmail.com</p>
          <p className='label_color'>Phone: 9677381857</p>
        </div>
      </div>
    </Router>
  );
};
